const addTag = <T extends RuleSet<any>>(arg: T): T & { isCss: true } =>
  Object.assign(arg, { isCss: true } as const);

function css(
  styles: Styles<object>,
  ...interpolations: Interpolation<object>[]
): RuleSet<object>;
